const items=document.querySelectorAll(".item"),placeholders=document.querySelectorAll(".placeholder");let current=0;for(const e of items)e.addEventListener("dragstart",dragstart),e.addEventListener("dragend",dragend);for(const e of placeholders)e.addEventListener("dragover",dragover),e.addEventListener("dragenter",dragenter),e.addEventListener("dragleave",dragleave),e.addEventListener("drop",dragdrop);function dragstart(e){current=e.target.dataset.index,e.target.classList.add("hold"),setTimeout(()=>{e.target.classList.add("hide")},0)}function dragend(e){e.target.className="item"}function dragover(e){e.preventDefault()}function dragenter(e){e.target.classList.add("hovered")}function dragleave(e){e.target.classList.remove("hovered")}function dragdrop(e){e.target.append(items[current]),e.target.classList.remove("hovered")}